import{parseBool as n,colorByStatus as s}from"./utils.min.js";export function popupHTML(t){const e=Array.isArray(t.inspecciones)?t.inspecciones.join(", "):"—";return`\n    <div style="font-size:13px;">\n      <strong>${t.nombre||"Negocio"}</strong><br/>\n      Estatus: <span style="color:${s(t.estatus)}; font-weight:600;">\n        ${t.estatus}\n      </span><br/>\n      Rubro: ${t.rubro} • CIIU: ${t.ciiu||"—"}<br/>\n      RUC: ${n(t.tiene_ruc)?"sí":"no"} • Patente: ${n(t.tiene_patente)?"sí":"no"}<br/>\n      Inspecciones: ${e}\n    </div>\n  `}
